"use strict";

// бургер

var burger = document.querySelector('.header__burger');
var menu = document.querySelector('.burger__menu');
var menuLinks = document.querySelectorAll('.burger__link');
var burgerBtn = document.querySelector('.burger__btn');
burger.addEventListener('click', function () {
  menu.style.visibility = 'visible';
  document.body.classList.toggle('stop-scroll');
  document.body.style.overflow = 'hidden';
});
menuLinks.forEach(function (element) {
  element.addEventListener('click', function () {
    burger.style.visibility = 'hidden';
    menu.style.visibility = 'hidden';
    document.body.style.overflow = 'visible';
  });
});
burgerBtn.addEventListener('click', function () {
  menu.style.visibility = 'hidden';
  document.body.style.overflow = 'visible';
});
"use strict";

//  лупа

var magnifierOpen = document.querySelector('.header__btn');
var magnifierClose = document.querySelector('.button_close');
var magnifierWindow = document.querySelector('.search_window');
magnifierOpen.addEventListener('click', function () {
  magnifierWindow.style.visibility = 'visible';
});
magnifierClose.addEventListener('click', function () {
  magnifierWindow.style.visibility = 'hidden';
});
"use strict";

// карта контактов

var mapPoint = document.querySelector('.map-point');
var contactsBtn = document.querySelector('.contacts__btn');
var address = document.querySelector('.contacts__address');
mapPoint.addEventListener('click', function () {
  address.style.visibility = 'visible';
  contactsBtn.style.visibility = 'visible';
});
contactsBtn.addEventListener('click', function () {
  address.style.visibility = 'hidden';
  contactsBtn.style.visibility = 'hidden';
});
"use strict";

// проверка имени и email

var inputName = document.querySelector(".input__name");
var nameError = document.querySelector('.input__name_error');
var inputEmail = document.getElementById('Email');
var emailError = document.getElementById('Email-error');
var regex = new RegExp(/[^A-Za-zА-яа-я]/gm);
var emailRegex = new RegExp(/\@/gm);
inputName.addEventListener('mouseleave', function () {
  var inputName = document.querySelector(".input__name");
  var result = regex.test(inputName.value);
  if (result) {
    nameError.style.visibility = 'visible';
    inputName.style.border = '1px solid #f06666';
  } else {
    nameError.style.visibility = 'hidden';
    inputName.style.border = 'none';
  }
});
inputEmail.addEventListener('mouseleave', function () {
  var inputEmail = document.getElementById('Email');
  var resultEmail = regex.test(inputEmail.value);
  if (!resultEmail) {
    emailError.style.visibility = 'visible';
    inputEmail.style.border = '1px solid #f06666';
  } else {
    emailError.style.visibility = 'hidden';
    inputEmail.style.border = 'none';
  }
});
"use strict";
